-- Generate a quad (coordinates) of each tile in the sprite sheet image
-- Need to start the loops at 0 in order to start at the beginning of the image
-- and then using the iteration counters to move alongside the image
function map.generate_quads(self)
    for row = 0, self.image_height / self.tile_height - 1 do 
        for column = 0 , self.image_width / self.tile_width -1 do
            table.insert(self.tiles, love.graphics.newQuad(column * self.tile_width, row * self.tile_width, self.tile_width, self.tile_height, self.image_width, self.image_height))
        end
    end
end

--Draw the tile map by placing a corresponding tile where a number is not 0 (not an empty tile)
function map.draw(self)
    love.graphics.draw(self.background, 0, 0, 0, (self.width * self.tile_width) / self.background:getWidth(), ((self.height - 4 + 2) * self.tile_height) / self.background:getHeight())
    for i = 1, self.height do
        for j = 1, self.width do
            if self.tile_map[((i - 1) * self.width) + j] ~= 0 then
                    love.graphics.draw(self.image, self.tiles[(self.tile_map[((i - 1) * self.width) + j])], (j - 1) * self.tile_width, (i - 1) * self.tile_height)
            end
        end
    end
end